import React from 'react';
import 'chart.js';
import 'chartjs-adapter-date-fns';
import { Line } from 'react-chartjs-2';
import { LinearScale, CategoryScale, PointElement, LineController, LinearScale as LinearScaleChart, Title } from 'chart.js';


const FrisbeeGolfGraph = ({ data }) => {
  Chart.register(LinearScale, CategoryScale, LineElement);
  const chartData = {
    labels: data.map((item, index) => `Hole ${index + 1}`),
    datasets: [
      {
        label: 'Frisbee Golf Scores',
        data: data,
        fill: false,
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  const chartOptions = {
    scales: {
      y: {
        type: 'linear', // Explicitly set the scale type
        beginAtZero: true,
        title: {
          display: true,
          text: 'Scores',
        },
      },
      x: {
        title: {
          display: true,
          text: 'Holes',
        },
      },
    },
  };

  return <Line data={chartData} options={chartOptions} />;
};

const App = () => {
  const frisbeeGolfData = [3, 2, 4, 5, 3, 2, 4, 3, 2, 4];

  return (
    <div>
      <h1>Ekeberg Frisbee Golf</h1>
      <FrisbeeGolfGraph data={frisbeeGolfData} />
    </div>
  );
};

export default App;
