import React from 'react';
import 'chart.js';
import 'chartjs-adapter-date-fns';
import { Line } from 'react-chartjs-2';
import { LinearScale, CategoryScale, PointElement, LineController, LinearScale as LinearScaleChart, Title, Chart } from 'chart.js';

const FrisbeeGolfGraph = ({ data }) => {
  Chart.register(LinearScaleChart, CategoryScale, PointElement, LineController, Title);
  const chartData = {
    labels: data[0].scores.map((item, index) => `Hole ${index + 1}`),
    datasets: data.map((player, index) => ({
      label: `Player ${index + 1}`,
      data: player.scores,
      fill: false,
      backgroundColor: `rgba(75, 192, 192, 0.6)`,
      borderColor: `rgba(75, 192, 192, 1)`,
      borderWidth: 1,
    })),
  };

  const chartOptions = {
    scales: {
      y: {
        type: 'linear',
        beginAtZero: true,
        title: {
          display: true,
          text: 'Scores',
        },
      },
      x: {
        title: {
          display: true,
          text: 'Holes',
        },
      },
    },
  };

  return <Line data={chartData} options={chartOptions} />;
};

const App = () => {
  const frisbeeGolfData = [
    { player: 1, scores: [3, 3, 5, 3, 3, 3, 3, 3, 2, 2, 2, 2, 3, 3, 4, 2, 2, 3] },
    { player: 2, scores: [2, 3, 4, 3, 2, 1, 4, 5, 3, 2] },
    { player: 3, scores: [4, 2, 3, 4, 2, 3, 1, 2, 3, 4] },
  ];

  return (
    <div>
      <h1>Ekeberg Frisbee Golf</h1>
      <FrisbeeGolfGraph data={frisbeeGolfData} />
    </div>
  );
};

export default App;
