import React, { useState } from 'react';
import 'chart.js';
import 'chartjs-adapter-date-fns';
import { Line } from 'react-chartjs-2';
import { LinearScale, CategoryScale, PointElement, LineElement, Chart } from 'chart.js';
import './frisbee.css';

Chart.register(LinearScale, CategoryScale, PointElement, LineElement);

const getInitialScores = (holeCount) => {
  const scores = [];
  for (let i = 0; i < holeCount; i++) {
    scores.push(3); // Set the initial scores to par values (3)
  }
  return scores;
};

const App = () => {
  const [games, setGames] = useState([]); // State to store the list of games
  const latestGame = games[games.length - 1]; // Get the latest game from the games array

  const addGame = () => {
    const newGame = {
      id: games.length + 1,
      parData: [3, 3, 4, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3],
      userScores: getInitialScores(18),
    };
    setGames([...games, newGame]);
  };

  const handleScoreChange = (gameId, index, score) => {
    if (score < 1) {
      // If the score is lower than 1, display an error message or take appropriate action
      alert("Score cannot be lower than 1");
      return;
    }

    setGames((prevGames) => {
      const updatedGames = prevGames.map((game) => {
        if (game.id === gameId) {
          const updatedScores = [...game.userScores];
          updatedScores[index] = score;
          return { ...game, userScores: updatedScores };
        }
        return game;
      });
      return updatedGames;
    });
  };

  return (
    <div>
      <h1>Ekeberg Frisbee Golf</h1>
      {latestGame && (
        <div key={latestGame.id}>
          <h2>Game {latestGame.id}</h2>
          <div>Total Par: {latestGame.parData.reduce((acc, score) => acc + score, 0)}</div>
          <div>Total Score: {latestGame.userScores.reduce((acc, score) => acc + score, 0)}</div>
          <Line
            data={{
              labels: latestGame.parData.map((_, index) => `Hole ${index + 1}`),
              datasets: [
                {
                  label: 'Par',
                  data: latestGame.parData,
                  borderColor: 'red',
                  borderWidth: 1,
                  fill: false,
                },
                {
                  label: 'User',
                  data: latestGame.userScores,
                  borderColor: 'blue',
                  borderWidth: 1,
                  fill: false,
                },
              ],
            }}
            options={{
              maintainAspectRatio: false, // Add this line to disable aspect ratio maintenance
              scales: {
                y: {
                  type: 'linear',
                  beginAtZero: true,
                  title: {
                    display: true,
                    text: 'Scores',
                  },
                  ticks: {
                    stepSize: 1,
                  },
                },
                x: {
                  title: {
                    display: true,
                    text: 'Holes',
                  },
                },
              },
            }}
          />

          <div>
            {latestGame.userScores.map((score, index) => (
              <input
                key={index}
                type="number"
                value={score}
                onChange={(e) =>
                  handleScoreChange(latestGame.id, index, parseInt(e.target.value))
                }
              />
            ))}
          </div>
        </div>
      )}
      <button onClick={addGame}>Add Game</button>
    </div>
  );
};

export default App;
