import React, { useState } from 'react';
import 'chart.js';
import 'chartjs-adapter-date-fns';
import { Line } from 'react-chartjs-2';
import { LinearScale, CategoryScale, PointElement, LineElement, Chart } from 'chart.js';

const FrisbeeGolfGraph = ({ datasets, onScoreChange }) => {
  Chart.register(LinearScale, CategoryScale, PointElement, LineElement);

  const totalPar = datasets[0].data.reduce((acc, score) => acc + score, 0);
  const totalScore = datasets.slice(1).reduce((acc, dataset) => {
    return acc + dataset.data.reduce((sum, score) => sum + score, 0);
  }, 0);

  const chartData = {
    labels: datasets[0].data.map((item, index) => `Hole ${index + 1}`),
    datasets: datasets.map((dataset, index) => ({
      label: dataset.label,
      data: dataset.data,
      borderColor: getLineColor(index),
      borderWidth: 1,
      fill: false,
    })),
  };
  const handleScoreUpdate = (index, score) => {
    if (score < 1) {
      score = 1; // Set the score to 1 if it's lower than 1
    }
    onScoreChange(index, score);
  }

  const chartOptions = {
    scales: {
      y: {
        type: 'linear',
        beginAtZero: true,
        title: {
          display: true,
          text: 'Scores',
        },
        ticks: {
          stepSize: 1,
        },
      },
      x: {
        title: {
          display: true,
          text: 'Holes',
        },
      },
    },
  };

  const userScores = datasets[1].data;
  const editableUserScores = userScores.slice(19);

  return (
    <div>
      <div>Total Par: {totalPar}</div>
      <div>Total Score: {totalScore}</div>
      <Line data={chartData} options={chartOptions} />
      <div>
        {editableUserScores.map((score, index) => (
          <input
            key={index}
            type="number"
            value={score}
            onChange={(e) => handleScoreUpdate(index + 19, parseInt(e.target.value))}
          />
        ))}
      </div>
    </div>
  );
};


// Helper function to get line color based on index
const getLineColor = (index) => {
  const colors = ['red', 'blue', 'green', 'orange']; // Add more colors if needed
  return colors[index % colors.length];
};

const App = () => {
  const parData = [3, 3, 4, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3, 3];

  const [userScores, setUserScores] = useState([...parData]); // Initialize user scores with par values

  const handleScoreChange = (index, score) => {
    if (score < 1) {
      // If the score is lower than 1, display an error message or take appropriate action
      alert("Score cannot be lower than 1");
      return;
    }

    const updatedScores = [...userScores];
    updatedScores[index] = score;
    setUserScores(updatedScores);
  };

  const dataset1 = {
    label: 'Par',
    data: parData,
  };

  const dataset2 = {
    label: 'User',
    data: userScores,
  };

  return (
    <div>
      <h1>Ekeberg Frisbee Golf</h1>
      <FrisbeeGolfGraph datasets={[dataset1, dataset2]} onScoreChange={handleScoreChange} />
      <div>
        {userScores.map((score, index) => (
          <input
            key={index}
            type="number"
            value={score}
            onChange={(e) => handleScoreChange(index, parseInt(e.target.value))}
          />
        ))}
      </div>
    </div>
  );
};

export default App;
