import React from 'react';
import 'chart.js';
import 'chartjs-adapter-date-fns';
import { Bar } from 'react-chartjs-2';
import { TimeScale, BarElement, Chart } from 'chart.js';

const FrisbeeGolfGraph = ({ datasets }) => {
  Chart.register(TimeScale, BarElement);

  const chartData = {
    labels: datasets[0].data.map((item, index) => `Hole ${index + 1}`),
    datasets: datasets.map((dataset, index) => ({
      label: dataset.label,
      data: dataset.data.map((value, index) => ({
        t: new Date().setHours(index), // Use a time value for x-axis
        y: value,
      })),
      backgroundColor: `rgba(75, 192, 192, 0.6)`,
      borderColor: `rgba(75, 192, 192, 1)`,
      borderWidth: 1,
    })),
  };

  const chartOptions = {
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Scores',
        },
      },
      x: {
        type: 'time', // Use the time scale for x-axis
        time: {
          unit: 'hour', // Display the time as hours
          displayFormats: {
            hour: 'H:mm', // Format for displaying hours
          },
        },
        title: {
          display: true,
          text: 'Holes',
        },
      },
    },
  };

  return <Bar data={chartData} options={chartOptions} />;
};

const App = () => {
  const dataset1 = {
    label: 'Player 1',
    data: [3, 2, 4, 5, 3, 2, 4, 3, 2, 4],
  };

  const dataset2 = {
    label: 'Player 2',
    data: [4, 3, 5, 4, 2, 3, 4, 2, 3, 5],
  };

  return (
    <div>
      <h1>Ekeberg Frisbee Golf</h1>
      <FrisbeeGolfGraph datasets={[dataset1, dataset2]} />
    </div>
  );
};

export default App;
