import React, { useEffect, useRef } from 'react';
import 'chart.js';
import 'chartjs-adapter-date-fns';
import { Chart } from 'chart.js';

const FrisbeeGolfGraph = ({ data }) => {
  const chartContainer = useRef(null);

  useEffect(() => {
    if (chartContainer && chartContainer.current) {
      const ctx = chartContainer.current.getContext('2d');
      const chart = new Chart(ctx, {
        type: 'line',
        data: {
          labels: data[0].scores.map((item, index) => `Hole ${index + 1}`),
          datasets: data.map((player, index) => ({
            label: `Player ${index + 1}`,
            data: player.scores,
            fill: false,
            backgroundColor: `rgba(75, 192, 192, 0.6)`,
            borderColor: `rgba(75, 192, 192, 1)`,
            borderWidth: 1,
          })),
        },
        options: {
          scales: {
            y: {
              type: 'linear',
              beginAtZero: true,
              title: {
                display: true,
                text: 'Scores',
              },
            },
            x: {
              title: {
                display: true,
                text: 'Holes',
              },
            },
          },
        },
      });

      return () => {
        chart.destroy();
      };
    }
  }, [data]);

  return <canvas ref={chartContainer} />;
};

const App = () => {
  const frisbeeGolfData = [
    { player: 1, scores: [3, 2, 4, 5, 3, 2, 4, 3, 2, 4] },
    { player: 2, scores: [2, 3, 4, 3, 2, 1, 4, 5, 3, 2] },
    { player: 3, scores: [4, 2, 3, 4, 2, 3, 1, 2, 3, 4] },
  ];

  return (
    <div>
      <h1>Ekeberg Frisbee Golf</h1>
      <FrisbeeGolfGraph data={frisbeeGolfData} />
    </div>
  );
};

export default App;
